// sign_test.go - SPHINCS-256 tests

package sphincs256

import (
	"bytes"
	"crypto/rand"
	"encoding/base64"
	"testing"
)

func TestGenerateKey(t *testing.T) {
	_, _, err := GenerateKey(rand.Reader)
	if err != nil {
		t.Fatalf("failed GenerateKey(): %s", err)
	}
}

func TestSignOpen(t *testing.T) {
	const msg = "Yog-Sothoth is the key and the guardian of the gate."

	pk, sk, err := GenerateKey(rand.Reader)
	if err != nil {
		t.Fatalf("failed GenerateKey(): %s", err)
	}

	signed := Sign(sk, []byte(msg))
	opened, err := Open(pk, signed)
	if err != nil {
		t.Fatalf("failed Open(): %s", err)
	}
	if bytes.Compare(opened, []byte(msg)) != 0 {
		t.Fatalf("opened message does not match test message")
	}
}

func TestKnownAnswer(t *testing.T) {
	// The known answer test values were generated using the "ref" SUPERCOP
	// implementation with a rigged randombytes() function that fills the
	// buffer with [0x00 0x01 ... 0xff 0x00 0x01 ...].

	// Test keypair generation vs the keypair generated with the reference code
	// with the same rigged randombytes() output.
	entropySource := make([]byte, PrivateKeySize)
	for i := 0; i < PrivateKeySize; i++ {
		entropySource[i] = byte(i & 0xff)
	}
	riggedRand := bytes.NewBuffer(entropySource)
	const expectedSkB64 = ""
	const expectedPkB64 = ""

	expectedSk, _ := base64.StdEncoding.DecodeString(expectedSkB64)
	expectedPk, _ := base64.StdEncoding.DecodeString(expectedPkB64)

	pk, sk, err := GenerateKey(riggedRand)
	if err != nil {
		t.Fatalf("failed GenerateKey(): %s", err)
	}

	if bytes.Compare(expectedSk, sk[:]) != 0 {
		t.Errorf("sk mismatch")
	}
	if bytes.Compare(expectedPk, pk[:]) != 0 {
		t.Errorf("pk mismatch")
	}

	// Test message signing and verification against a signature generated with
	// the previous keypair.
	const msg = "Cthulhu Fthagn --What a wonderful phrase!Cthulhu Fthagn --Say it and you're crazed!"
	const expectedSmB64 = ""

	expectedSm, _ := base64.StdEncoding.DecodeString(expectedSmB64)
	sm := Sign(sk, []byte(msg))
	if bytes.Compare(expectedSm, sm) != 0 {
		t.Errorf("sm mismatch")
	}

	opened, err := Open(pk, expectedSm)
	if err != nil {
		t.Fatalf("failed Open(): %s", err)
	}
	if bytes.Compare(opened, []byte(msg)) != 0 {
		t.Fatalf("opened message does not match test message")
	}
}

var benchMsg = []byte("In relating the circumstances which have led to my confinement within this refuge for the demented, I am aware that my present position will create a natural doubt of the authenticity of my narrative.")

func BenchmarkSign(b *testing.B) {
	pk, sk, err := GenerateKey(rand.Reader)
	if err != nil {
		b.Fatalf("failed GenerateKey(): %s", err)
	}
	b.ResetTimer()

	for i := 0; i < b.N; i++ {
		sm := Sign(sk, benchMsg)
		b.StopTimer()

		opened, err := Open(pk, sm)
		if err != nil {
			b.Fatalf("failed Open(): %s", err)
		}
		if bytes.Compare(opened, benchMsg) != 0 {
			b.Fatalf("opened message does not match test message")
		}
		b.StartTimer()
	}
}

func BenchmarkOpen(b *testing.B) {
	pk, sk, err := GenerateKey(rand.Reader)
	if err != nil {
		b.Fatalf("failed GenerateKey(): %s", err)
	}
	sm := Sign(sk, benchMsg)
	b.ResetTimer()

	for i := 0; i < b.N; i++ {
		opened, err := Open(pk, sm)
		if err != nil {
			b.Fatalf("failed Open(): %s", err)
		}
		b.StopTimer()

		if bytes.Compare(opened, benchMsg) != 0 {
			b.Fatalf("opened message does not match test message")
		}
		b.StartTimer()
	}

}
